mapper.select:
select
    count(*)
from bas_agent_in_out_money


        and agent_id = #{agentId}


        and biz_type = #{bizType}


        and type = #{type}


        and DATE_FORMAT(create_time, '%Y-%c') =#{queryTime}


        and create_time LIKE concat('%', #{statsDate}, '%')

select
   *
from bas_agent_in_out_money


        and agent_id = #{agentId}


        and biz_type = #{bizType}


        and type = #{type}


        and DATE_FORMAT(create_time, '%Y-%c') =#{queryTime}


        and create_time LIKE concat('%', #{statsDate}, '%')


order by id desc
limit ${beginIndex}, ${rows}
