mapper.select:
SELECT config_value
FROM bas_system_config
WHERE id=#{id}

select id, config_name as configName, config_value as configValue, is_read_only as isReadOnly, is_show as isShow, value_type as valueType from bas_system_config
and is_show = #{isShow}
and config_name LIKE concat('%', #{configName}, '%')
and category_type = #{categoryType}
order by category_type
