mapper.select:
select
*
from hdg_battery_order_battery_report_log_${suffix}
where order_id = #{orderId}
AND lat is not null AND lat != 0 AND lng is not null AND lng != 0 AND coordinate_type is not null AND coordinate_type != 'gprs'

    and report_time >= #{startDate}


    and report_time <= #{endDate}

order by report_time asc

select
*
from hdg_battery_order_battery_report_log_${suffix}
where order_id = #{orderId}
AND lat is not null AND lat != 0 AND lng is not null AND lng != 0 AND coordinate_type is not null AND coordinate_type != 'gprs'

    and report_time >= #{queryBeginTime}


    and report_time <= #{queryEndTime}

order by report_time asc
limit ${beginIndex}, ${rows}

select
count(*)
from hdg_battery_order_battery_report_log_${suffix}
where order_id = #{orderId}
AND lat is not null AND lat != 0 AND lng is not null AND lng != 0 AND coordinate_type is not null AND coordinate_type != 'gprs'

    and report_time >= #{startDate}


    and report_time <= #{endDate}

select
*
from hdg_battery_order_battery_report_log_${suffix}
where order_id LIKE concat(#{orderId}, '%')
order by report_time desc
limit ${beginIndex}, ${rows}
