mapper.select:
select
count(*)
from hdg_battery_order


        and agent_id = #{agentId}


        and id > #{beginPrefixId}


        and id < #{endPrefixId}


        and take_cabinet_id = #{takeCabinetId}

select
count(*) as num,
DATE_FORMAT(create_time, '%Y-%m-%d') as `date`
from hdg_battery_order


        and agent_id = #{agentId}


        and id > #{beginPrefixId}


        and id < #{endPrefixId}


        and take_cabinet_id = #{takeCabinetId}


GROUP BY
DATE_FORMAT(create_time, '%Y-%m-%d')

select
count(*)
from hdg_battery_order


        and agent_id = #{agentId}


        and partner_id = #{partnerId}


        and take_shop_id = #{takeShopId}


        and pay_type = #{payType}


        and id LIKE concat(#{id}, '%')


        and customer_id = #{customerId}


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and customer_fullname LIKE concat(#{customerFullname}, '%')


        and order_status = #{orderStatus}


        and battery_id LIKE concat(#{batteryId}, '%')


        and take_cabinet_id LIKE concat(#{takeCabinetId}, '%')


        and put_cabinet_id LIKE concat(#{putCabinetId}, '%')


        and create_time >= #{queryBeginTime}


        and create_time <= #{queryEndTime}


        and take_time >= #{takeBeginTime}


        and take_time <= #{takeEndTime}


        and put_time >= #{putBeginTime}


        and put_time <= #{putEndTime}


        and (take_shop_id is not null and take_shop_id != "")


        and  vehicle_order_flag = 0


        and  vehicle_order_flag =1

select
t1.*, t2.shell_code as shellCode
from hdg_battery_order t1 left join hdg_battery t2
on t1.battery_id = t2.id


        and t1.agent_id = #{agentId}


        and t1.partner_id = #{partnerId}


        and t1.take_shop_id = #{takeShopId}


        and t1.pay_type = #{payType}


        and t1.id LIKE concat(#{id}, '%')


        and t1.customer_id = #{customerId}


        and t1.customer_mobile LIKE concat(#{customerMobile}, '%')


        and t1.customer_fullname LIKE concat(#{customerFullname}, '%')


        and t1.order_status = #{orderStatus}


        and t1.battery_id LIKE concat(#{batteryId}, '%')


        and t1.take_cabinet_id LIKE concat(#{takeCabinetId}, '%')


        and t1.put_cabinet_id LIKE concat(#{putCabinetId}, '%')


        and t1.create_time >= #{queryBeginTime}


        and t1.create_time <= #{queryEndTime}


        and t1.take_time >= #{takeBeginTime}


        and t1.take_time <= #{takeEndTime}


        and t1.put_time >= #{putBeginTime}


        and t1.put_time <= #{putEndTime}


        and (t1.take_shop_id is not null and take_shop_id != "")


        and  t1.vehicle_order_flag = 0


        and  t1.vehicle_order_flag =1


order by t1.id desc
limit ${beginIndex}, ${rows}

select
  count(1)
from hdg_battery_order left join bas_customer on hdg_battery_order.customer_id = bas_customer.id


        and hdg_battery_order.agent_id = #{agentId}


        and hdg_battery_order.customer_mobile LIKE concat(#{customerMobile}, '%')


        and hdg_battery_order.customer_fullname LIKE concat(#{customerFullname}, '%')

    and hdg_battery_order.order_status in (5)

        and pay_time >= #{queryBeginTime}


        and pay_time <= #{queryEndTime}

select
*
from hdg_battery_order left join bas_customer on hdg_battery_order.customer_id = bas_customer.id


        and hdg_battery_order.agent_id = #{agentId}


        and hdg_battery_order.customer_mobile LIKE concat(#{customerMobile}, '%')


        and hdg_battery_order.customer_fullname LIKE concat(#{customerFullname}, '%')

      and hdg_battery_order.order_status in (5)

        and pay_time >= #{queryBeginTime}


        and pay_time <= #{queryEndTime}


order by hdg_battery_order.id desc
limit ${beginIndex}, ${rows}

mapper.update:
update hdg_battery_order set
refund_status = #{refundStatus},
refund_money = #{refundMoney},
refund_time = #{refundTime},
refund_reason = #{refundReason}
where id = #{id}

update hdg_battery_order set

        pay_timeout_fault_log_id = null where pay_timeout_fault_log_id = #{value}


        not_take_timeout_fault_log_id = null where not_take_timeout_fault_log_id = #{value}
