mapper.select:
select
count(id)
from chg_cabinet_install_record


        and agent_id = #{agentId}


        and agent_name LIKE concat('%', #{agentName}, '%')


        and cabinet_id = #{cabinetId}


        and cabinet_name LIKE  concat('%', #{cabinetName}, '%')


        and status = #{status}

select
*
from chg_cabinet_install_record


        and agent_id = #{agentId}


        and agent_name LIKE concat('%', #{agentName}, '%')


        and cabinet_id = #{cabinetId}


        and cabinet_name LIKE  concat('%', #{cabinetName}, '%')


        and status = #{status}


limit ${beginIndex}, ${rows}

select * from chg_cabinet_install_record
where 1 = 1

    and agent_id = #{agentId}
