mapper.select:
select
count(1)
from hdg_dispatcher_area
where 1 = 1

    and agent_id = #{agentId}


    and user_id = #{userId}

select id, agent_id as agentId, user_id as userId, province_id as provinceId, city_id as cityId, district_id as districtId, create_time as createTime from hdg_dispatcher_area where 1 = 1
and agent_id= #{agentId}
and user_id = #{userId}
limit #{beginIndex}, #{rows}
