mapper.select:
select count(*)
from zc_shop_price_setting
where shop_id = #{shopId} AND price_setting_id = #{priceSettingId}

select count(*)
from zc_shop_price_setting AS zsps LEFT JOIN zc_price_setting AS zps ON zsps.price_setting_id = zps.id
LEFT JOIN zc_vehicle_model AS zvm ON zps.model_id = zvm.id


        and zps.setting_name LIKE concat('%', #{settingName}, '%')


        and zsps.agent_id = #{agentId}


        and zsps.shop_id = #{shopId}

select zsps.*, zps.category AS category, zps.vehicle_name AS vehicleName, zvm.model_name AS modelName, zps.is_active AS isActive, zps.setting_name AS settingName, zps.model_id AS modelId, zps.battery_count AS batteryCount from zc_shop_price_setting AS zsps LEFT JOIN zc_price_setting AS zps ON zsps.price_setting_id = zps.id LEFT JOIN zc_vehicle_model AS zvm ON zps.model_id = zvm.id
and zps.setting_name LIKE concat('%', #{settingName}, '%')
and zsps.agent_id = #{agentId}
and zsps.shop_id = #{shopId}
order by zsps.id desc limit ${beginIndex}, ${rows}

