mapper.select:
select * from hdg_shop where 1 = 1 AND is_vehicle = 1
and shop_name LIKE concat('%', #{shopName}, '%')
and agent_id = #{agentId}
and id LIKE concat(#{id}, '%')
and id not in (select shop_id from zc_vehicle_vip_price_shop)
order by id desc limit ${beginIndex}, ${rows}

select * from hdg_shop where 1 = 1
and id LIKE concat(#{id}, '%')
and shop_name LIKE concat('%', #{shopName}, '%')
and agent_id = #{agentId}
order by id desc limit ${beginIndex}, ${rows}

mapper.update:
update hdg_shop
set
id = #{shopId}

    ,shop_name = #{shopName}


    ,province_id = #{provinceId}


    ,city_id = #{cityId}


    ,district_id = #{districtId}


    ,geo_hash = #{geoHash}


    ,address = #{address}


    ,lng = #{lng}


    ,lat = #{lat}

where id = #{shopId}

UPDATE hdg_shop SET pay_people_name = #{payPeopleName}, pay_people_mp_open_id = #{payPeopleMpOpenId}, pay_people_fw_open_id = #{payPeopleFwOpenId},
pay_password = #{payPassword},
pay_people_mobile = #{payPeopleMobile} where id = #{id}

update hdg_shop set
min_price = #{minPrice},
max_price = #{maxPrice}
where id = #{id}
