mapper.select:
select ifnull(SUM(money),0) as money
from  zd_rent_foregift_order
where agent_id = #{agentId}

    #{item}

SELECT
    COUNT(*)
FROM zd_rent_foregift_order
where 1 = 1

    and partner_id = #{partnerId}


    and agent_id = #{agentId}


     and customer_fullname LIKE concat('%', #{customerFullname}, '%')


     and customer_mobile LIKE concat(#{customerMobile}, '%')


     and id LIKE concat(#{id}, '%')


    and status != #{defaultQueryStatus}


    and status = #{status}


    and customer_id = #{customerId}


    and create_time BETWEEN  #{queryBeginTime} AND #{queryEndTime} 


    and  vehicle_order_flag = #{vehicleOrderFlag}


    and  vehicle_order_flag =0

SELECT
    *
FROM zd_rent_foregift_order
WHERE 1 = 1

    and partner_id = #{partnerId}


    and agent_id = #{agentId}


     and customer_fullname LIKE concat('%', #{customerFullname}, '%')


     and customer_mobile LIKE concat(#{customerMobile}, '%')


     and id LIKE concat(#{id}, '%')


    and status != #{defaultQueryStatus}


    and status = #{status}


    and customer_id = #{customerId}


    and create_time BETWEEN  #{queryBeginTime} AND #{queryEndTime} 


    and  vehicle_order_flag = #{vehicleOrderFlag}


    and  vehicle_order_flag =0

order by id desc
limit ${beginIndex}, ${rows}

select *
from  zd_rent_foregift_order
where customer_id = #{customerId} and agent_id = #{agentId}

mapper.update:
update zd_rent_foregift_order
set pay_time = #{payTime}
where id = #{id}

update zd_rent_foregift_order set
    status = #{toStatus},
    memo = #{memo},
    apply_refund_time = #{applyRefundTime}
where id = #{id}
and status = #{fromStatus}
