mapper.select:
select count(*) from zd_vip_rent_price
and agent_id = #{agentId}
and price_name LIKE concat('%', #{priceName}, '%')

select * from zd_vip_rent_price
and agent_id = #{agentId}
and price_name LIKE concat('%', #{priceName}, '%')
order by id desc limit #{beginIndex}, #{rows}

mapper.update:
update zd_vip_rent_price set
customer_count = #{customerCount}
where id = #{id}

update zd_vip_rent_price set
shop_count = #{shopCount}
where id = #{id}

update zd_vip_rent_price set
agent_company_count = #{agentCompanyCount}
where id = #{id}
