mapper.select:
select *
from hdg_bespeak_order
where customer_id = #{customerId}
 and status =  #{status}

select *
from hdg_bespeak_order
where bespeak_cabinet_id = #{bespeakCabinetId}
and bespeak_box_num =  #{bespeakBoxNum}
and status =  #{status}
limit 1

mapper.update:
update hdg_bespeak_order
set status = #{toStatus},
cancel_time = #{cancelTime}
where id = #{id} and status = #{fromStatus}
