mapper.select:
select count(*)
from bas_alipay_pay_order
left join bas_customer on bas_alipay_pay_order.customer_id = bas_customer.id


        and bas_alipay_pay_order.agent_id = #{agentId}


        and bas_alipay_pay_order.source_type = #{sourceType}


        and bas_alipay_pay_order.order_status = #{orderStatus}


        and  bas_customer.fullname LIKE concat(#{customerName}, '%')


        and  bas_alipay_pay_order.source_id LIKE concat('%', #{sourceId}, '%')


        and bas_alipay_pay_order.partner_id = #{partnerId}


        and bas_alipay_pay_order.create_time   LIKE concat('%', #{statsDate}, '%')

select bas_alipay_pay_order.id, bas_alipay_pay_order.agent_id as agentId, bas_alipay_pay_order.customer_id as customerId, bas_alipay_pay_order.money, bas_alipay_pay_order.source_type as sourceType, bas_alipay_pay_order.source_id as sourceId, bas_alipay_pay_order.order_status as orderStatus, bas_alipay_pay_order.payment_id as paymentId, bas_customer.mobile, bas_alipay_pay_order.handle_time as handleTime, bas_alipay_pay_order.create_time as createTime, bas_customer.fullname as customerName from bas_alipay_pay_order left join bas_customer on bas_alipay_pay_order.customer_id = bas_customer.id
and bas_alipay_pay_order.agent_id = #{agentId}
and bas_alipay_pay_order.source_type = #{sourceType}
and bas_alipay_pay_order.order_status = #{orderStatus}
and bas_customer.fullname LIKE concat(#{customerName}, '%')
and bas_alipay_pay_order.source_id LIKE concat('%', #{sourceId}, '%')
and bas_alipay_pay_order.partner_id = #{partnerId}
and bas_alipay_pay_order.create_time LIKE concat('%', #{statsDate}, '%')
ORDER by bas_alipay_pay_order.create_time DESC limit ${beginIndex}, ${rows}

select bas_alipay_pay_order.id, bas_alipay_pay_order.customer_id as customerId, bas_alipay_pay_order.money, bas_alipay_pay_order.source_id as sourceId, bas_alipay_pay_order.source_type as sourceType, bas_alipay_pay_order.order_status as orderStatus, bas_alipay_pay_order.payment_id as paymentId, bas_customer.mobile, bas_alipay_pay_order.handle_time as handleTime, bas_alipay_pay_order.refund_time as refundTime, bas_alipay_pay_order.refund_money as refundMoney, bas_customer.fullname as customerName from bas_alipay_pay_order join hdg_packet_period_order on bas_alipay_pay_order.source_id = hdg_packet_period_order.id left join bas_customer on bas_alipay_pay_order.customer_id = bas_customer.id
and hdg_packet_period_order.order_refund_id = #{orderRefundId}
ORDER by bas_alipay_pay_order.create_time DESC limit ${beginIndex}, ${rows}

mapper.update:
 update bas_alipay_pay_order set
 order_status = #{toStatus},
refund_money = IFNULL(refund_money,0) + #{refundMoney},
 refund_time = #{refundTime}
 where id = #{id}
