mapper.select:
select b.id, b.code, b.version, b.bms_model from bas_battery_upgrade_pack_detail as a left join hdg_battery_parameter as b on a.battery_id = b.id
a.upgrade_pack_id = #{upgradePackId}
limit ${beginIndex}, ${rows}

mapper.insert:
INSERT INTO bas_battery_upgrade_pack_detail
(
   upgrade_pack_id,
   battery_id
) VALUES (
    #{upgradePackId},
    #{batteryId}
)
