mapper.select:
select id, pack_type as packType, upgrade_name as upgradeName, file_name as fileName, file_path as filePath, old_version as oldVersion, new_version as newVersion, size, md5_sum as md5Sum, memo, update_time as updateTime from bas_charger_upgrade_pack
and upgrade_name LIKE concat('%' , #{upgradeName} , '%')
limit ${beginIndex}, ${rows}
