mapper.select:
select count(id) from bas_laxin_record t1
inner join bas_laxin_pay_order_detail t2 on t1.id = t2.record_id
and agent_id = #{agentId}
and laxin_mobile LIKE concat(#{laxinMobile}, '%')
and target_mobile LIKE concat(#{targetMobile}, '%')
and t2.order_id = #{orderId}

select
t1.*
from bas_laxin_record t1

    inner join bas_laxin_pay_order_detail t2 on t1.id = t2.record_id



        and  agent_id = #{agentId}


        and laxin_mobile LIKE concat(#{laxinMobile}, '%')


        and target_mobile LIKE concat(#{targetMobile}, '%')


        and t2.order_id = #{orderId}


order by id desc
limit #{beginIndex}, #{rows}

mapper.update:
update bas_laxin_record set
    status = #{toStatus},
    mp_open_id = #{mpOpenId},
    account_name = #{accountName}
where id = #{id} and status = #{fromStatus}

update bas_laxin_record set
status = #{status},
cancel_canuse = #{cancelCanuse}
where id = #{id}
