mapper.select:
select count(*) from bas_push_message left join bas_push_message_content on bas_push_message.id = bas_push_message_content.id
and source_type = #{sourceType}
and source_id LIKE concat('%', #{sourceId}, '%')
and send_status = #{sendStatus}
and bas_push_message_content.target LIKE concat('%', #{target}, '%')
and bas_push_message_content.content LIKE concat('%', #{content}, '%')

select bas_push_message.id, agent_id as agentId, send_status as sendStatus, source_type as sourceType, source_id as sourceId, create_time as createTime, handle_time as handleTime, resend_num as resendNum, bas_push_message_content.target as target, bas_push_message_content.content as content from bas_push_message left join bas_push_message_content on bas_push_message.id = bas_push_message_content.id
and source_type = #{sourceType}
and source_id LIKE concat('%', #{sourceId}, '%')
and send_status = #{sendStatus}
and bas_push_message_content.target LIKE concat('%', #{target}, '%')
and bas_push_message_content.content LIKE concat('%', #{content}, '%')
order by bas_push_message.id desc limit ${beginIndex}, ${rows}
