mapper.select:
select
count(*)
from bas_resignation


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and cabinet_id = #{cabinetId}


        and agent_id = #{agentId}

select
*
from bas_resignation


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and cabinet_id = #{cabinetId}


        and agent_id = #{agentId}


order by create_time desc
limit ${beginIndex}, ${rows}
