mapper.select:
select
count(*)
from bas_weixinmp_pay_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and source_type = #{sourceType}


        and order_status = #{orderStatus}


        and mobile LIKE concat(#{mobile}, '%')


        and customer_name  LIKE concat('%', #{customerName}, '%')


        and source_id  LIKE concat('%', #{sourceId}, '%')


        and id   LIKE concat('%', #{id}, '%')


        and create_time   LIKE concat('%', #{statsDate}, '%')

select
*
from bas_weixinmp_pay_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and source_type = #{sourceType}


        and order_status = #{orderStatus}


        and mobile LIKE concat(#{mobile}, '%')


        and customer_name  LIKE concat('%', #{customerName}, '%')


        and source_id  LIKE concat('%', #{sourceId}, '%')


        and id   LIKE concat('%', #{id}, '%')


        and create_time   LIKE concat('%', #{statsDate}, '%')


ORDER BY create_time DESC
limit ${beginIndex}, ${rows}

select bas_weixinmp_pay_order.id, bas_weixinmp_pay_order.customer_id as customerId, bas_weixinmp_pay_order.money, bas_weixinmp_pay_order.source_id as sourceId, bas_weixinmp_pay_order.source_type as sourceType, bas_weixinmp_pay_order.order_status as orderStatus, bas_weixinmp_pay_order.payment_id as paymentId, bas_customer.mobile, bas_weixinmp_pay_order.handle_time as handleTime, bas_weixinmp_pay_order.refund_time as refundTime, bas_weixinmp_pay_order.refund_money as refundMoney, bas_customer.fullname as customerName from bas_weixinmp_pay_order join hdg_packet_period_order on bas_weixinmp_pay_order.source_id = hdg_packet_period_order.id left join bas_customer on bas_weixinmp_pay_order.customer_id = bas_customer.id
and hdg_packet_period_order.order_refund_id = #{orderRefundId}
ORDER by bas_weixinmp_pay_order.create_time DESC limit ${beginIndex}, ${rows}

