mapper.select:
select * from hdg_battery_cell where 1 = 1
and cell_mfr LIKE concat('%',#{cellMfr}, '%')
and cell_model LIKE concat('%',#{cellModel}, '%')
and barcode LIKE concat('%',#{barcode}, '%')
and battery_id = #{batteryId}
order by create_time desc limit ${beginIndex}, ${rows}

mapper.update:
update hdg_battery_cell set
battery_id = null
where id = #{id}

update hdg_battery_cell set
battery_id = null
where battery_id = #{batteryId}

update hdg_battery_cell set
battery_id = #{batteryId}
where id = #{id}
