mapper.select:
select
count(id)
from hdg_battery_install_record


        and agent_id = #{agentId}


        and status = #{status}

select
*
from hdg_battery_install_record


        and agent_id = #{agentId}


        and status = #{status}


limit ${beginIndex}, ${rows}

mapper.update:
UPDATE hdg_battery_install_record SET
      status = #{status}
WHERE id = #{id}
