mapper.select:
select
*
from hdg_battery_report_log_${suffix}
where battery_id = #{batteryId}
and report_time = #{reportTime}

select
count(*)
from hdg_battery_report_log_${suffix}
where battery_id = #{batteryId}

    and DATE_FORMAT(report_time, '%Y-%m-%d' ) = DATE_FORMAT( #{reportTime}, '%Y-%m-%d')

select
*
from hdg_battery_report_log_${suffix}
where battery_id = #{batteryId}

    and DATE_FORMAT(report_time, '%Y-%m-%d' ) = DATE_FORMAT( #{reportTime}, '%Y-%m-%d')

order by report_time desc
limit ${beginIndex}, ${rows}

select
*
from hdg_battery_report_log_${suffix}
where battery_id = #{batteryId}

    and DATE_FORMAT(report_time, '%Y-%m-%d' ) = DATE_FORMAT( #{reportTime}, '%Y-%m-%d')
