mapper.select:
select
count(id)
from hdg_cabinet_operate_log


        and agent_id = #{agentId}


        and cabinet_name LIKE concat('%', #{cabinetName}, '%')


        and operate_type = #{operateType}


        and operator_type = #{operatorType}


        and box_num = #{boxNum}

select
*
from hdg_cabinet_operate_log


        and agent_id = #{agentId}


        and cabinet_name LIKE concat('%', #{cabinetName}, '%')


        and operate_type = #{operateType}


        and operator_type = #{operatorType}


        and box_num = #{boxNum}


order by id desc
limit #{beginIndex}, #{rows}
