mapper.select:
select
count(*)
from hdg_exchange_installment_setting
where mobile = #{mobile}

    and id != #{id}

select count(*) from hdg_exchange_installment_setting heis
where 1 = 1

    and heis.agent_id = #{agentId}


    and heis.fullname LIKE concat(#{fullname}, '%')


    and heis.setting_type = #{settingType}


    and heis.is_active = #{isActive}


    and heis.mobile LIKE concat(#{mobile}, '%')


  and EXISTS (select 1 FROM hdg_exchange_installment_customer heic WHERE heic.setting_id = heis.id and heic.customer_mobile LIKE concat(#{customermobile}, '%'))


    and EXISTS(select 1 FROM hdg_exchange_installment_cabinet heic2 WHERE heic2.setting_id = heis.id and heic2.cabinet_id LIKE concat(#{cabinetId}, '%'))


    and EXISTS(select 1 FROM hdg_exchange_installment_station heis2 WHERE heis2.setting_id = heis.id and heis2.station_id LIKE concat(#{stationId}, '%'))

select * from hdg_exchange_installment_setting heis where 1 = 1
and heis.agent_id = #{agentId}
and heis.fullname LIKE concat(#{fullname}, '%')
and heis.setting_type = #{settingType}
and heis.is_active = #{isActive}
and heis.mobile LIKE concat(#{mobile}, '%')
and EXISTS (select 1 FROM hdg_exchange_installment_customer heic WHERE heic.setting_id = heis.id and heic.customer_mobile LIKE concat(#{customermobile}, '%'))
and EXISTS(select 1 FROM hdg_exchange_installment_cabinet heic2 WHERE heic2.setting_id = heis.id and heic2.cabinet_id LIKE concat(#{cabinetId}, '%'))
and EXISTS(select 1 FROM hdg_exchange_installment_station heis2 WHERE heis2.setting_id = heis.id and heis2.station_id LIKE concat(#{stationId}, '%'))
order by create_time desc limit ${beginIndex}, ${rows}
