mapper.select:
select
count(*)
from hdg_insurance_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and status = #{status}


        and status != #{status}


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and customer_fullname LIKE concat(#{customerFullname}, '%')


        and pay_time >= #{queryBeginTime}


        and pay_time <= #{queryEndTime}

select
*
from hdg_insurance_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and status = #{status}


        and status = #{status}


        and status != #{status}


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and customer_fullname LIKE concat(#{customerFullname}, '%')


        and pay_time >= #{queryBeginTime}


        and pay_time <= #{queryEndTime}


order by hdg_insurance_order.id desc
limit ${beginIndex}, ${rows}

mapper.update:
update hdg_insurance_order
set status = #{status}
where customer_id = #{customerId}

update hdg_packet_period_order
set pay_time = #{payTime}
where id = #{id}

update hdg_insurance_order
set refund_money = #{refundMoney},
refund_time = #{refundTime},
status = #{toStatus}
where id = #{id}
