mapper.select:
select material_name from (select material_name from yms_material where

    agent_id = #{value}


    group_id = #{value}


    material_id = #{value}

limit 1
) t

select
    count(id)
from yms_material


        and agent_id = #{agentId}


        and material_type = #{materialType}


        and group_id = #{groupId}


        and material_name LIKE concat('%', #{materialName}, '%')


        and owner_name LIKE concat('%', #{ownerName}, '%')

select
    *
from yms_material


        and agent_id = #{agentId}


        and material_type = #{materialType}


        and group_id = #{groupId}


        and material_name LIKE concat('%', #{materialName}, '%')


        and owner_name LIKE concat('%', #{ownerName}, '%')


        and convert_status = #{convertStatus}


order by id DESC
limit #{beginIndex}, #{rows}

mapper.update:
update yms_material set
    group_id = #{groupId}
where id = #{id}

update yms_material set
    group_id = #{groupId},
    duration = #{duration},
    version = #{version}
where id = #{id}
