mapper.select:
select
    count(*)
from hdg_cabinet hc
inner join yms_terminal_online yto on yto.id = hc.terminal_id


        and hc.agent_id = #{agentId}


        and yto.id LIKE concat('%', #{id})


        and yto.is_online = #{isOnline}


        and hc.group_id = #{groupId}


        and hc.cabinet_name LIKE concat('%', #{cabinetName})

select yto.id, yto.cpu, yto.memory, yto.play_volume as playVolume, yto.card_capacity as cardCapacity, yto.rest_capacity as restCapacity, yto.heart_time as heartTime, yto.is_normal as isNormal, yto.is_online as isOnline, yto.status_info as statusInfo, yto.strategy_uid as strategyUid, yto.download_progress as downloadProgress, yto.speed, hc.id as cabinetId, hc.cabinet_name as cabinetName, hc.agent_id as agentId, hc.address as address from hdg_cabinet hc inner join yms_terminal_online yto on yto.id = hc.terminal_id
and hc.agent_id = #{agentId}
and yto.id LIKE concat('%', #{id})
and yto.is_online = #{isOnline}
and hc.group_id = #{groupId}
and hc.cabinet_name LIKE concat('%', #{cabinetName})
limit #{beginIndex}, #{rows}

mapper.update:
update yms_terminal_online set
is_online = 0
where id = #{id}
