mapper.select:
select count(*) from zc_vehicle_online_stats
where 1 = 1

    and vehicle_id = #{vehicleId}

select hvos.*, zv.vin_no AS vinNo from zc_vehicle_online_stats AS hvos LEFT JOIN zc_vehicle AS zv ON hvos.vehicle_id = zv.id where 1 = 1
and hvos.vehicle_id = #{vehicleId}
order by begin_time desc limit ${beginIndex}, ${rows}
