mapper.select:
SELECT
    COUNT(*)
FROM zd_rent_period_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and customer_fullname LIKE concat('%', #{customerFullname}, '%')


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and status= #{status}


        and  vehicle_order_flag = #{vehicleOrderFlag}


        and  vehicle_order_flag = 0

SELECT
    *
FROM zd_rent_period_order


        and partner_id = #{partnerId}


        and agent_id = #{agentId}


        and customer_fullname LIKE concat('%', #{customerFullname}, '%')


        and customer_mobile LIKE concat(#{customerMobile}, '%')


        and status= #{status}


        and  vehicle_order_flag = #{vehicleOrderFlag}


        and  vehicle_order_flag = 0


ORDER BY create_time DESC
limit #{beginIndex}, #{rows}

SELECT
    COUNT(*)
FROM zd_rent_period_order
LEFT JOIN bas_customer ON zd_rent_period_order.customer_id = bas_customer.id


        and zd_rent_period_order.agent_id = #{agentId}


        and zd_rent_period_order.customer_mobile LIKE concat(#{customerMobile}, '%')


        and zd_rent_period_order.customer_fullname LIKE concat(#{customerFullname}, '%')

    and zd_rent_period_order.status in (2,3)

        and zd_rent_period_order.create_time >= #{queryBeginTime} 


        and zd_rent_period_order.create_time < #{queryEndTime}

select
    zd_rent_period_order.*
from zd_rent_period_order
left join bas_customer on zd_rent_period_order.customer_id = bas_customer.id


        and zd_rent_period_order.agent_id = #{agentId}


        and zd_rent_period_order.customer_mobile LIKE concat(#{customerMobile}, '%')


        and zd_rent_period_order.customer_fullname LIKE concat(#{customerFullname}, '%')

    and zd_rent_period_order.status in (2,3)

        and zd_rent_period_order.create_time >= #{queryBeginTime} 


        and zd_rent_period_order.create_time < #{queryEndTime} 


ORDER BY create_time DESC
limit #{beginIndex}, #{rows}

select * FROM zd_rent_period_order where customer_id = #{customerId} and status in
#{item}
order by end_time desc limit 1

mapper.update:
update zd_rent_period_order
set refund_money = #{refundMoney},
refund_time = #{refundTime},
status = #{toStatus}
where id = #{id}

update zd_rent_period_order
set refund_money = null,
refund_time = null,
status = #{toStatus}
where id = #{id} and status = #{fromStatus}

update zd_rent_period_order
set status = #{status}
where customer_id = #{customerId}

update zd_rent_period_order
set pay_time = #{payTime}
where id = #{id}

update zd_rent_period_order
set day_count = #{dayCount},
end_time = #{endTime},
status = #{status},
operator_memo = #{operatorMemo}
where id = #{id}
